mapper.select:
select b.bno,
b.title, b.content, b.regdate, b.count, b.category, b.videopath,
b.imagepath
from (
select rownum rn, a.*
from (
select *
from ${tblContents}
where ${colCategory} = #{category}
order by bno desc
) a
) b
where rn between #{start} and #{end}

	select a.Bno, a.Title, a.Count, b.Menuname , b.Href
	from (
	select *
	from ${tblContents}
	where ${colCategory} = #{category}
 	order by ${colCount} desc
 	) a join ${tblMenu} b
	on a.category = b.href
	where rownum between 1 and 5

select count(bno) cnt from ${tblContents}
where


		${colTitle} like #{keyword}
		and ${colCategory} = #{category}


		${colTitle} like #{keyword}
		or
		${colContent} like #{keyword}
		and
		${colCategory} = #{category}

select b.bno, b.title, b.content, b.regdate, b.count, b.category,
b.videopath, b.imagepath
from (
select rownum rn, a.*
from (
select *
from ${tblContents}
where


		${colTitle} like #{keyword}
		and ${colCategory} = #{category}


		${colTitle} like #{keyword}
		or
		${colContent} like #{keyword}
		and ${colCategory} = #{category}


order by bno desc
) a
) b
where rn between #{start} and #{end}
