mapper.select:
select b.bno, b.title, b.content, b.regdate, b.count, b.category, b.videopath, b.imagepath
from (
    select rownum rn, a.*
    from (
        select * 
        from ${tblContents} 
        where ${colCategory} = #{category} 
        order by bno desc
    ) a
) b
where rn between #{start} and #{end}

select count(bno) cnt from ${tblContents}
where


    	${colTitle} like #{keyword}
    	and ${colCategory} = #{category}


    	${colTitle} like #{keyword}
    	or
    	${colContent} like #{keyword}
    	and
    	${colCategory} = #{category}

select b.bno, b.title, b.content, b.regdate, b.count, b.category, b.videopath, b.imagepath
from (
    select rownum rn, a.*
    from (
        select * 
        from ${tblContents} 
        where


        		${colTitle} like #{keyword}


        		${colTitle} like #{keyword}
        		or
        		${colContent} like #{keyword}


        order by bno desc
    ) a
) b
where rn between #{start} and #{end}

		select rownum rn, a.*
		from (
   			select * 
   			from ${tblFree} 
   			order by fcount desc
		) a
		where rownum between 1 and 5

		select rownum rn, a.*
		from (
   			select * 
   			from ${tblContents} 
   			where ${colCategory} = 'movie'
   			order by count desc
		) a
		where rownum between 1 and 5

		select rownum rn, a.*
		from (
   			select * 
   			from ${tblContents} 
   			where ${colCategory} = 'drama'
   			order by count desc
		) a
		where rownum between 1 and 5

		select rownum rn, a.*
		from (
   			select * 
   			from ${tblContents} 
   			where ${colCategory} = 'animation'
   			order by count desc
		) a
		where rownum between 1 and 5

		select rownum rn, a.*
		from (
   			select * 
   			from ${tblContents} 
   			where ${colCategory} = 'variety'
   			order by count desc
		) a
		where rownum between 1 and 5
